# accepted on codewars.com
en_alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'

# Array containing the relative frequency of each letter in the English language
english_frequencies = [0.08167, 0.01492, 0.02782, 0.04253, 0.12702, 0.02228, 0.02015,
                       0.06094, 0.06966, 0.00153, 0.00772, 0.04025, 0.02406, 0.06749,
                       0.07507, 0.01929, 0.00095, 0.05987, 0.06327, 0.09056, 0.02758,
                       0.00978, 0.02360, 0.00150, 0.01974, 0.00074]


def get_keyword(ciphertext, key_len):

    def get_curr_shift(sequence_in: str):

        chi_squared_deviations = [0] * len(en_alphabet)

        for backshift in range(len(en_alphabet)):

            chi_squared_deviation = 0

            sequence_shifted_back = [en_alphabet[(en_alphabet.index(ch) - backshift) % len(en_alphabet)] for ch in sequence_in]

            actual_frequency = [0] * len(en_alphabet)

            for symbol in sequence_shifted_back:

                actual_frequency[en_alphabet.index(symbol)] += 1

            for n in range(len(en_alphabet)):

                actual_frequency[n] /= float(len(sequence_shifted_back))

            for n in range(len(en_alphabet)):

                chi_squared_deviation += ((actual_frequency[n] - english_frequencies[n]) ** 2) / english_frequencies[n]

            chi_squared_deviations[backshift] = chi_squared_deviation

        real_backshift = chi_squared_deviations.index(min(chi_squared_deviations))

        return en_alphabet[real_backshift]

    keyword = ''

    for j in range(0, key_len):
        sequence = ''

        for i in range(j, len(ciphertext), key_len):
            sequence += ciphertext[i]

        print(f'seq: {sequence}')

        keyword += get_curr_shift(sequence)

    return keyword


key = "CODEWARS"
ciphertext = ""

print(get_keyword(ciphertext, len(key)))
